(81, 'store_setup_survey_survey_q2_2022', 'survey', 'en_US', 'How is your store setup going?', 'Our goal is to make sure you have all the right tools to start setting up your store in the smoothest way possible.\r\nWe’d love to know if we hit our mark and how we can improve. To collect your thoughts, we made a 2-minute survey.', '{}', 'pending', 'woocommerce.com', '2022-05-10 05:34:43', NULL, 0, 'plain', '', 0, 'info', 0),
(82, 'affirm_q2_2022', 'marketing', 'en_US', 'Boost your business with flexible payments', 'Expand your customer base with smarter payment options for more shoppers. With Affirm,  you can offer the most relevant payment options at every price point – from four interest-free payments every two weeks to longer installments up to 36 months. Fast-track your revenue goals today!', '{}', 'pending', 'woocommerce.com', '2022-05-15 06:05:17', NULL, 0, 'plain', '', 0, 'info', 0),
(83, 'wc-admin-EU-consumer-protection', 'marketing', 'en_US', 'Important changes to EU consumer protection laws', 'New regulations to help modernize and strengthen consumer protection laws in the European Union (EU) take effect on May 28, 2022. These rules impact all merchants selling to the EU, regardless of where their business is located. Further detailed information is available on the European Commission\'s official website.', '{}', 'unactioned', 'woocommerce.com', '2022-05-15 06:05:17', NULL, 0, 'plain', '', 0, 'info', 0),
(84, 'googlelistings_multicountrygtm_q22022', 'marketing', 'en_US', 'Expand your audience with Multi-Country Advertising', 'It\'s time to expand your audience with Multi-Country Advertising, an exciting new feature in Google Listings &amp; Ads. Reach more potential customers in as many regions as you like within a single campaign, all from the convenience of your WooCommerce dashboard.', '{}', 'unactioned', 'woocommerce.com', '2022-05-16 06:02:17', NULL, 0, 'plain', '', 0, 'info', 0),
