(1, 'wc-admin-navigation-nudge', 'info', 'en_US', 'You now have access to the WooCommerce navigation', 'We’re introducing a new navigation for a more intuitive and improved user experience. You can enable the beta version of the new experience in the Advanced Settings. Enable it now for your store.', '{}', 'unactioned', 'woocommerce-admin', '2022-06-17 07:26:48', NULL, 0, 'plain', '', 1, 0, 'info'),
(2, 'wc-admin-complete-store-details', 'info', 'en_US', 'Add your store details to complete store setup', 'Complete your store details with important information for setup such as your store’s base address', '{}', 'unactioned', 'woocommerce-admin', '2022-06-17 07:26:48', NULL, 0, 'plain', '', 1, 0, 'info'),
(3, 'new_in_app_marketplace_2021', 'info', 'en_US', 'Customize your store with extensions', 'Check out our NEW Extensions tab to see our favorite extensions for customizing your store, and discover the most popular extensions in the WooCommerce Marketplace.', '{}', 'unactioned', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 1, 0, 'info'),
(4, 'wayflyer_bnpl_q4_2021', 'marketing', 'en_US', 'Grow your business with funding through Wayflyer', 'Fast, flexible financing to boost cash flow and help your business grow – one fee, no interest rates, penalties, equity, or personal guarantees. Based on your store’s performance, Wayflyer provides funding and analytical insights to invest in your business.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(5, 'wc_shipping_mobile_app_usps_q4_2021', 'marketing', 'en_US', 'Print and manage your shipping labels with WooCommerce Shipping and the WooCommerce Mobile App', 'Save time by printing, purchasing, refunding, and tracking shipping labels generated by <a href=\"https://woocommerce.com/woocommerce-shipping/\">WooCommerce Shipping</a> – all directly from your mobile device!', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(6, 'wc_shipping_mobile_app_q4_2021', 'marketing', 'en_US', 'Print and manage your shipping labels with the WooCommerce Mobile App', 'Save time by printing, purchasing, refunding, and tracking shipping labels generated by <a href=\"https://woocommerce.com/woocommerce-shipping/\">WooCommerce Shipping</a> – all directly from your mobile device!', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(7, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(8, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store \"ready to sell\" as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(9, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they\'re shopping on your online store. No more follow-up email requests—customers get what they want, before they\'re done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(10, 'wc-admin-getting-started-in-ecommerce', 'info', 'en_US', 'Getting Started in eCommerce - webinar', 'We want to make eCommerce and this process of getting started as easy as possible for you. Watch this webinar to get tips on how to have our store up and running in a breeze.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(11, 'your-first-product', 'info', 'en_US', 'Your first product', 'That’s huge! You’re well on your way to building a successful online store — now it’s time to think about how you’ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.', '{}', 'unactioned', 'woocommerce.com', '2022-06-17 20:15:29', NULL, 0, 'plain', '', 1, 0, 'info'),
(12, 'wc-admin-optimizing-the-checkout-flow', 'info', 'en_US', 'Optimizing the checkout flow', 'It’s crucial to get your store’s checkout as smooth as possible to avoid losing sales. Let’s take a look at how you can optimize the checkout experience for your shoppers.', '{}', 'unactioned', 'woocommerce.com', '2022-07-01 10:54:35', NULL, 0, 'plain', '', 1, 0, 'info'),
(13, 'wc-admin-first-five-things-to-customize', 'info', 'en_US', 'The first 5 things to customize in your store', 'Deciding what to start with first is tricky. To help you properly prioritize, we’ve put together this short list of the first few things you should customize in WooCommerce.', '{}', 'unactioned', 'woocommerce.com', '2022-07-01 10:54:35', NULL, 0, 'plain', '', 1, 0, 'info'),
(14, 'wc-payments-qualitative-feedback', 'info', 'en_US', 'WooCommerce Payments setup - let us know what you think', 'Congrats on enabling WooCommerce Payments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(15, 'share-your-feedback-on-paypal', 'info', 'en_US', 'Share your feedback on PayPal', 'Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(16, 'google_listings_and_ads_install', 'marketing', 'en_US', 'Drive traffic and sales with Google', 'Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(17, 'wc-subscriptions-security-update-3-0-15', 'info', 'en_US', 'WooCommerce Subscriptions security update!', 'We recently released an important security update to WooCommerce Subscriptions. To ensure your site’s data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help — just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(18, 'woocommerce-core-update-5-4-0', 'info', 'en_US', 'Update to WooCommerce 5.4.1 now', 'WooCommerce 5.4.1 addresses a checkout issue discovered in WooCommerce 5.4. We recommend upgrading to WooCommerce 5.4.1 as soon as possible.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(19, 'wcpay-promo-2020-11', 'marketing', 'en_US', 'wcpay-promo-2020-11', 'wcpay-promo-2020-11', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(20, 'wcpay-promo-2020-12', 'marketing', 'en_US', 'wcpay-promo-2020-12', 'wcpay-promo-2020-12', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(21, 'ppxo-pps-upgrade-paypal-payments-1', 'info', 'en_US', 'Get the latest PayPal extension for WooCommerce', 'Heads up! There’s a new PayPal on the block!<br /><br />Now is a great time to upgrade to our latest <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal extension</a> to continue to receive support and updates with PayPal.<br /><br />Get access to a full suite of PayPal payment methods, extensive currency and country coverage, and pay later options with the all-new PayPal extension for WooCommerce.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(22, 'ppxo-pps-upgrade-paypal-payments-2', 'info', 'en_US', 'Upgrade your PayPal experience!', 'Get access to a full suite of PayPal payment methods, extensive currency and country coverage, offer subscription and recurring payments, and the new PayPal pay later options.<br /><br />Start using our <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">latest PayPal today</a> to continue to receive support and updates.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(23, 'woocommerce-core-sqli-july-2021-need-to-update', 'update', 'en_US', 'Action required: Critical vulnerabilities in WooCommerce', 'In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(24, 'woocommerce-blocks-sqli-july-2021-need-to-update', 'update', 'en_US', 'Action required: Critical vulnerabilities in WooCommerce Blocks', 'In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(25, 'woocommerce-core-sqli-july-2021-store-patched', 'update', 'en_US', 'Solved: Critical vulnerabilities patched in WooCommerce', 'In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(26, 'woocommerce-blocks-sqli-july-2021-store-patched', 'update', 'en_US', 'Solved: Critical vulnerabilities patched in WooCommerce Blocks', 'In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(27, 'habit-moment-survey', 'marketing', 'en_US', 'We’re all ears! Share your experience so far with WooCommerce', 'We’d love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(28, 'ecomm-wc-navigation-survey', 'info', 'en_US', 'We’d like your feedback on the WooCommerce navigation', 'We’re making improvements to the WooCommerce navigation and would love your feedback. Share your experience in this 2 minute survey.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(29, 'woocommerce-core-paypal-march-2022-updated', 'update', 'en_US', 'Security auto-update of WooCommerce', '<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy PayPal Standard security updates for stores running WooCommerce (version 3.5 to 6.3). It’s recommended to disable PayPal Standard, and use <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal Payments</a> to accept PayPal.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(30, 'woocommerce-core-paypal-march-2022-updated-nopp', 'update', 'en_US', 'Security auto-update of WooCommerce', '<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy security updates related to PayPal Standard payment gateway for stores running WooCommerce (version 3.5 to 6.3).', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(31, 'pinterest_03_2022_update', 'marketing', 'en_US', 'Your Pinterest for WooCommerce plugin is out of date!', 'Update to the latest version of Pinterest for WooCommerce to continue using this plugin and keep your store connected with Pinterest. To update, visit <strong>Plugins &gt; Installed Plugins</strong>, and click on “update now” under Pinterest for WooCommerce.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(32, 'setup_task_initiative_survey_q2_2022', 'survey', 'en_US', 'We want to know what matters most to you', 'Take 2 minutes to give us your input on what is important for you while setting up your store and help shape the future of WooCommerce together.', '{}', 'unactioned', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 1, 0, 'info'),
(33, 'store_setup_survey_survey_q2_2022', 'survey', 'en_US', 'How is your store setup going?', 'Our goal is to make sure you have all the right tools to start setting up your store in the smoothest way possible.\r\nWe’d love to know if we hit our mark and how we can improve. To collect your thoughts, we made a 2-minute survey.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(34, 'wc-admin-EU-consumer-protection', 'marketing', 'en_US', 'Important changes to EU consumer protection laws', 'New regulations to help modernize and strengthen consumer protection laws in the European Union (EU) take effect on May 28, 2022. These rules impact all merchants selling to the EU, regardless of where their business is located. Further detailed information is available on the European Commission\'s official website.', '{}', 'unactioned', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 1, 0, 'info'),
(35, 'wc-admin-wisepad3', 'marketing', 'en_US', 'Take your business on the go in Canada with WooCommerce In-Person Payments', 'Quickly create new orders, accept payment in person for orders placed online, and automatically sync your inventory – no matter where your business takes you. With WooCommerce In-Person Payments and the WisePad 3 card reader, you can bring the power of your store anywhere.', '{}', 'pending', 'woocommerce.com', '2022-06-17 07:26:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(36, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2022-06-17 07:26:56', NULL, 0, 'plain', '', 1, 0, 'info'),
(37, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2022-06-17 07:26:59', NULL, 0, 'plain', '', 1, 0, 'info'),
(38, 'surface_cart_checkout', 'info', 'en_US', 'Introducing the Cart and Checkout blocks!', 'Increase your store\'s revenue with the conversion optimized Cart &amp; Checkout WooCommerce blocks available in the WooCommerce Blocks extension.', '{}', 'unactioned', 'woo-gutenberg-products-block', '2022-06-17 07:26:59', NULL, 0, 'plain', '', 1, 0, 'info'),
(39, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'به ووکامرس کمک کنید تا با پیگیری اطلاعات فروشگاه شما بهبود پیدا کند', 'جمع آوری داده های استفاده به ما امکان بهبود WooCommerce را می دهد. با ارزیابی ویژگی های جدید ، قضاوت در مورد کیفیت به روزرسانی یا تعیین منطقی بودن پیشرفت ، فروشگاه شما مورد توجه قرار خواهد گرفت. شما همیشه می توانید از <a href=\"http://localhost/store-wholesale-and-retail/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\"> تنظیمات </a> بازدید کرده و اشتراک گذاری داده ها را متوقف کنید. <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\"> بیشتر بخوانید </a> درمورد اینکه چه داده ای را جمع می کنیم.', '{}', 'unactioned', 'woocommerce-admin', '2022-06-25 19:31:11', NULL, 0, 'plain', '', 1, 0, 'info'),
(40, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'آیا می‌دانستید؟', 'یک فروشگاه مجهز به ووکامرس برای دریافت اولین فروش به طور متوسط 31 روز زمان نیاز دارد. شما در مسیر درستی هستید! آیا این نوع بینش را مفید می دانید؟', '{}', 'unactioned', 'woocommerce-admin', '2022-06-25 19:31:12', NULL, 0, 'plain', '', 1, 0, 'info'),
(41, 'wc-admin-launch-checklist', 'info', 'en_US', 'آماده اجرای فروشگاه هستید؟', 'برای اطمینان از اینکه هرگز احساس فرو رفتن \"چه چیزی را فراموش کردم\" نیستید ، ما چک لیست ضروری قبل از راه اندازی را تهیه کرده ایم.', '{}', 'unactioned', 'woocommerce-admin', '2022-06-25 19:31:12', NULL, 0, 'plain', '', 1, 0, 'info'),
(42, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'ویرایش محصول شما چقدر آسان بود؟', 'شما می خواهید کاتالوگ و تصاویر محصول شما عالی و مطابق با نام تجاری شما باشد. این راهنما به شما نکاتی را می دهد که می خواهید محصولات شما در فروشگاه عالی جلوه کند.', '{}', 'unactioned', 'woocommerce-admin', '2022-06-25 19:31:12', NULL, 0, 'plain', '', 1, 0, 'info'),
(43, 'TikTok q2_2022', 'marketing', 'en_US', 'Give your store a stage on the world’s fastest-growing advertising channel', 'With TikTok for WooCommerce, you can sync your catalog, create videos, and track performance in front of TikTok’s one billion global users. Try the Smart Video Generator to make ads using your existing product images – no camera needed. Get $200 in ad credit from TikTok after a $20 spend (terms &amp; conditions apply).', '{}', 'unactioned', 'woocommerce.com', '2022-07-01 10:54:35', NULL, 0, 'plain', '', 1, 0, 'info'),
(44, 'tiktok-webinar-promo-july2022', 'marketing', 'en_US', 'Free webinar: TikTok for WooCommerce', 'Join WooCommerce and TikTok live on July 14 to learn about ad types, trends, and the best ways to connect with an audience of one billion active users on the world’s fastest-growing social platform. Participants have a chance to score a free month of Vimeo Pro, and two winners will get a $5,000 ad credit and more.', '{}', 'pending', 'woocommerce.com', '2022-07-10 15:40:42', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'wc-update-db-reminder', 'update', 'en_US', 'به‌روزرسانی پایگاه داده ووکامرس انجام شد', 'دیتای ووکامرس بروز شد. از شما برای بروزرسانی به آخرین نگارش سپاسگزاریم!', '{}', 'actioned', 'woocommerce-core', '2022-07-18 07:25:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'mercado_pago_q3_2022', 'marketing', 'en_US', 'Get paid with Mercado Pago Checkout', 'Give your customers a checkout they can trust with Latin America\'s leading payment processor. Securely accept debit and credit cards, cash, bank transfers, and installment payments – backed by exclusive fraud prevention tools.', '{}', 'pending', 'woocommerce.com', '2022-08-16 18:07:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'klarna_q3_2022', 'marketing', 'en_US', 'Meet Klarna – your ultimate growth partner', 'Increase conversions by offering secure, flexible payment solutions – including buy now, pay later – all through a one-click checkout experience. Plus, you’ll tap into a whole new market of the world’s most engaged shoppers.', '{}', 'pending', 'woocommerce.com', '2022-08-16 18:07:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-payments-august-2022-need-to-update', 'update', 'en_US', 'Action required: Please update WooCommerce Payments', 'An updated secure version of WooCommerce Payments is available – please ensure that you’re using the latest patch version. For more information on what action you need to take, please review the article below.', '{}', 'pending', 'woocommerce.com', '2022-08-16 18:07:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-payments-august-2022-store-patched', 'update', 'en_US', 'WooCommerce Payments has been automatically updated', 'You’re now running the latest secure version of WooCommerce Payments. We’ve worked with the WordPress Plugins team to deploy a security update to stores running WooCommerce Payments (version 3.9 to 4.5). For further information, please review the article below.', '{}', 'pending', 'woocommerce.com', '2022-08-16 18:07:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'mobile_app_order_management_q3_2022', 'marketing', 'en_US', 'Take order management on the go', 'The WooCommerce Mobile App continues to get better with added order management functionality! Add or remove products, edit fees, or change the shipping options in any existing order – all from within the app.', '{}', 'unactioned', 'woocommerce.com', '2022-08-21 19:36:16', NULL, 0, 'plain', '', 1, 0, 'info'),
(51, 'product_creation_usability_test_3_months', 'survey', 'en_US', 'Have a say in the future of WooCommerce', 'Take an early look at the future of editing products in WooCommerce. Share your thoughts (~5 minutes) and help shape the experience for yourself and other merchants.', '{}', 'unactioned', 'woocommerce.com', '2022-08-25 18:26:32', NULL, 0, 'plain', '', 1, 0, 'info'),
(52, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'هشدارهای سفارش بلادرنگ را در هر مکان دریافت کنید', 'با برنامه Woo اعلان های مربوط به فعالیت فروشگاه ، از جمله سفارشات جدید و بررسی محصولات را مستقیماً در دستگاه های تلفن همراه خود دریافت کنید.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-18 16:18:41', NULL, 0, 'plain', '', 1, 0, 'info'),
(53, 'googlelistings_signals2022_hasGLA', 'marketing', 'en_US', 'Show off your products with Ads on Google', 'You’re ready to grow with ads. Google optimizes for performance across your products; you only pay for results. If you’re new to Google, you can earn up to $500 in ad credits (T&amp;Cs apply). <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">Contact support</a> if you need guidance with Google Listings &amp; Ads.', '{}', 'pending', 'woocommerce.com', '2022-09-26 08:35:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'googlelistings_signals2022_noGLA', 'marketing', 'en_US', 'Show off your products with Ads on Google', 'You’re ready to grow with ads. Google optimizes for performance across your products; you only pay for results. If you’re new to Google, you can earn up to $500 in ad credits (T&amp;Cs apply). <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">Contact support</a> if you need guidance with Google Listings &amp; Ads.', '{}', 'pending', 'woocommerce.com', '2022-09-26 08:35:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'wc-admin-orders-milestone', 'info', 'en_US', 'اولین سفارش دریافت شد', 'اولین سفارش خود را ثبت کردید به شما تبریک می گوییم! اکنون زمان خوبی برای یادگیری نحوه مدیریت سفارشات است.', '{}', 'unactioned', 'woocommerce-admin', '2022-10-10 06:27:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'shipping_category_q4_2022', 'marketing', 'en_US', 'Save time on shipping', 'Is your store all set to ship? Save valuable time (and money!) by automating your fulfillment process for the busiest shopping season. Explore our range of trusted shipping partners to get started.', '{}', 'unactioned', 'woocommerce.com', '2022-11-05 13:11:37', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce_admin_deprecation_q4_2022', 'info', 'en_US', 'WooCommerce Admin is part of WooCommerce!', 'To make sure your store continues to run smoothly, check that WooCommerce is up-to-date – at least version 6.5 – and then disable the WooCommerce Admin plugin.', '{}', 'pending', 'woocommerce.com', '2022-11-05 13:11:37', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'tiktok-targeted-q4-2022', 'marketing', 'en_US', 'Get $200 in ad credit from TikTok after you spend $20 on your first campaign', 'Reach one billion shoppers with TikTok for WooCommerce this holiday season! Sync your product catalog, capture insights, and create ad campaigns right from your dashboard. Connect your store today to unlock this limited time offer! <a href=\"https://ads.tiktok.com/help/article?aid=10011326\">Terms &amp; conditions apply</a>.', '{}', 'pending', 'woocommerce.com', '2022-11-05 13:11:37', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'paypal_paylater_g3_q4_22', 'marketing', 'en_US', 'Turn browsers into buyers with Pay Later', 'Add PayPal at checkout, plus give customers a buy now, pay later option from the name they trust. With Pay in 4 &amp; Pay Monthly, available in PayPal Payments, you get paid up front while letting customers spread their payments over time. Boost your average order value and convert more sales – at no extra cost to you.', '{}', 'pending', 'woocommerce.com', '2022-11-05 13:11:37', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'paypal_paylater_g2_q4_22', 'marketing', 'en_US', 'Upgrade to PayPal Payments to offer Pay Later at checkout', 'PayPal Pay Later is included in PayPal Payments at no additional cost to you. Customers can spread their payments over time while you get paid up front. \r\nThere’s never been a better time to upgrade your PayPal plugin. Simply download it and connect with a PayPal Business account.', '{}', 'pending', 'woocommerce.com', '2022-11-05 13:11:37', NULL, 0, 'plain', '', 0, 0, 'info');
