INSERT INTO `wp_wpam_messages` (`messageId`, `name`, `use`, `content`, `type`) VALUES
(1, 'affiliate_application_approved', 'Displayed to user at logon if affiliate STATUS = APPROVED', 'Congratulations, the administrator has <strong>approved</strong> your application. You have one more step to complete before you can begin publishing for this store and generating revenue! The store owner has specified the terms of your agreement, which you will need to review and agree to.<br /><br />', 'web'),
(2, 'affiliate_application_declined', 'Displayed to user at logon if affiliate STATUS = DECLINED', 'I\'m sorry, your application was declined.', 'web'),
(3, 'affiliate_application_pending', 'Displayed to user at logon if affiliate STATUS = PENDING', 'Your application is still being reviewed. Please check back later!', 'web'),
(4, 'affiliate_application_submitted', 'Displayed to user after successfully submitting the affiliate registration form', 'Your application has been submitted. The store owner will be contacting you soon.<br /><br />Thank you!<br />', 'web'),
(5, 'affiliate_application_submitted_email', 'Body of e-mail sent to the affiliate immediately after submitting their application.', 'Your application will be reviewed and you will be hearing from us soon!', 'email'),
(6, 'affiliate_application_declined_email', 'Body of e-mail sent to the affiliate immediately following their application being declined.', 'I\'m sorry, your application was declined.', 'email'),
(7, 'aff_app_submitted_auto_approved', 'Displayed to a newly registered affiliate if automatic affiliate approval option is enabled.', 'Thank you. Your registration is now complete. You can log into the affiliate area and begin promoting.', 'web'),
(8, 'affiliate_application_approved_email', 'Body of e-mail sent to a newly registered affiliate immediately following their application being approved.', 'Your affiliate account for {blogname} has been approved!. \n\nUsername: {affusername} \nPassword: {affpassword} \nLogin URL: {affloginurl} \n\nPlease log into your account to get referral code.', 'email'),
(9, 'affiliate_commission_notification_email', 'Body of e-mail sent to the affiliate immediately after earning a commission.', 'Great news, you have just earned a commission by sending a referral to our site! \n\nPlease log into your affiliate account to view the details. \n\nThank You', 'email'),
(10, 'admin_affiliate_commission_notification_email', 'Body of e-mail sent to the admin when an affiliate earns a commission.', 'An affiliate just earned a commission by sending a referral to the site. \n\nPlease log into your WordPress admin dashboard to view the details.', 'email'),
(11, 'aff_approved_email_existing_user_account', 'Body of e-mail sent to a newly approved affiliate if they have an existing user account.', 'New affiliate registration for {blogname} has been approved! \n\nLog into the site with your existing account and get started.', 'email');
